import React, { Component } from "react";
import Table from './Table.js'
import Map from './Map.js'
// import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(props) {
      super(props);
      this.state = { Nodes : [], aMap : {} };
  }

  async callAPI() {
    const response = await fetch('http://34.220.181.170:9000/peers/get', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ example: 'data' }),
    })
    var resJson = await response.json()
    var nodes = resJson.Nodes
     if (nodes && nodes.length > 0) {
      var map = {}
      nodes.map((node, index) => {
        map[node.Details._id] = index
      })
      this.setState({Nodes : nodes, aMap : map})
    }
  }

  componentDidMount() {
    this.callAPI();
  }

  render() {
    console.log(this.state.Nodes)
    return (
      <div className="App">
        <Table Nodes={this.state.Nodes} aMap = {this.state.aMap}/>
        <Map/>
      </div>
    );
  }
}

export default App;
