import React, { Component } from "react";
import Table from './Table.js'
import Map from './Map.js'
// import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(props) {
      super(props);
      this.state = { Nodes : [], chosen : -1, conn : []};
  }

  async callAPI() {
    const response = await fetch('http://34.220.181.170:9000/peers/get', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ example: 'data' }),
    })
    var resJson = await response.json()
    var nodes = resJson.Nodes
     if (nodes && nodes.length > 0) {
      var map = {}
      nodes.map((node, index) => {
        map[node.Details._id] = index
      })
      this.aMap = map
      this.setState({Nodes : nodes, chosen : -1, conn : []})
    }
  }

  componentDidMount() {
    this.callAPI();
  }

  handleClick(i) {
    var conn = []
    var node = this.props.Nodes[i]
    node.Details._peer.map((id) => {
       if (this.aMap[id] !== undefined) {
          conn.push(this.aMap[id])
       }
    })
    this.props.onClick(i)
    this.setState({chosen : i, connected : conn})
  }

  render() {
    console.log(this.state.Nodes)
    return (
      <div className="App">
        <Table Nodes={this.state.Nodes} conn = {this.state.conn} onClick={i => this.handleClick(i)} chosen={this.state.chosen}/>
        <Map/>
      </div>
    );
  }
}

export default App;
