import React, { Component } from 'react'

class Table extends Component {
   constructor(props) {
      super(props)//since we are extending class Table so we have to use super in order to override Component class constructor
      this.state = {
         choosen : -1,
         connected : []
      }
   }

   handleClick(i) {
      var conn = []
      var node = this.props.Nodes[i]
      console.log("xj2 i = " + i + " node is " + node)
      node.Details._peer.map((id) => {
         if (this.props.aMap[id] != undefined) {
            conn.push(this.props.aMap[id])
         }
      })
      this.setState({choosen : i, connected : conn})
      alert(i)
   }

   renderTableData() {
      console.log(this.props.Nodes)
      return this.props.Nodes.map((node, index) => {
         var details = node.Details;
         var i = index
         var bcolor = "background-normal"
         console.log("xj1 i = " + i + " choosen is " + this.state.choosen)
         if (i == this.state.choosen) {
            bcolor = "background-blue"
         }
         this.state.connected.map((num) => {bcolor = "background-red"})
         return (
            <tr key={details._id} className={bcolor}>
               <td>{details._id}</td>
               <td>{details._ip}</td>
               <td>{details._city}</td>
               <td>{details._latitude}</td>
               <td>{details._longitude}</td>
               <td>
                  <button className="showPeersButtton" onClick={index => this.handleClick(i)} >
                     Show Peers 
                  </button>
               </td>
            </tr>
         )
      })
   }

   renderTableHeader() {
      let header = ["ID","IP","CITY", "LATITUDE", "LONGITUDE"]
      return header.map((key, index) => {
         return <th key={index}>{key.toUpperCase()}</th>
      })
   }

   render() { //Whenever our class runs, render method will be called automatically, it may have already defined in the constructor behind the scene.
      if (this.props.Nodes){
         return (
            <div>
               <h1>React Dynamic Table</h1>
               <table id='nodeTable'>
                   <tbody>
                       <tr>{this.renderTableHeader()}</tr>
                       {this.renderTableData()}
                   </tbody>
               </table>
            </div>
         )
      }
      return(
         <div/>
      )
   }
}

export default Table
