import React, { Component } from 'react'

class Table extends Component {
   constructor(props) {
      super(props)//since we are extending class Table so we have to use super in order to override Component class constructor
   }

   handleClick(i) {
      // var conn = []
      // var node = this.props.Nodes[i]
      // node.Details._peer.map((id) => {
      //    if (this.props.aMap[id] !== undefined) {
      //       conn.push(this.props.aMap[id])
      //    }
      // })
      // this.setState({chosen : i, connected : conn})
      this.props.onClick(i)
   }

   renderTableData() {
      console.log(this.props.nodes)
      return this.props.nodes.map((node, index) => {
         var details = node.Details;
         var i = index
         var bcolor = "background-normal"
         this.props.connected.map((num) => {
            if (i === num) {
               bcolor = "background-red"
            }
         })
         if (i === this.props.chosen) {
            bcolor = "background-blue"
         }
         return (
            <tr key={details._id} className={bcolor}>
               <td>{details._id}</td>
               <td>{details._ip}</td>
               <td>{details._city}</td>
               <td>{details._country}</td>
               <td>{details._latitude}</td>
               <td>{details._longitude}</td>
               <td>
                  <button className="showPeersButtton" onClick={() => this.handleClick(i)} >
                     Show Peers 
                  </button>
               </td>
            </tr>
         )
      })
   }

   renderTableHeader() {
      let header = ["ID","IP","CITY","Country","LATITUDE","LONGITUDE"]
      return header.map((key, index) => {
         return <th key={index}>{key.toUpperCase()}</th>
      })
   }

   renderInfo(){
      if (this.props.nodes && this.props.chosen > 0){
         var peers = this.props.nodes[this.props.chosen].Details._peer
         return peers.map((id) => {
            return <tr><td>{id}</td></tr>
         })
      }
      return (
         <tr/>
      )
   }

   render() { //Whenever our class runs, render method will be called automatically, it may have already defined in the constructor behind the scene.
      if (this.props.nodes){
         return (
            <div>
               <h1>Guardian Node Table</h1>
               <table className='nodeTable'>
                   <tbody>
                       <tr>{this.renderTableHeader()}</tr>
                       {this.renderTableData()}
                   </tbody>
               </table>
               <div className="InfoBar">
                  <h3 className="title">Peers connected</h3>
                  <table>
                     <tbody>
                        {this.renderInfo()}
                     </tbody>
                  </table>
               </div>
            </div>
         )
      }
      return(
         <div/>
      )
   }
}

export default Table
